mapper.insert:
INSERT INTO member (
    user_id, file_idx, dept_idx, lv_idx, password, name, email, gender, hire_date
) VALUES (
    #{user_id}, #{file_idx}, #{dept_idx}, #{lv_idx}, #{password}, #{name}, #{email}, #{gender}, #{hire_date}
)

mapper.select:
SELECT user_id, file_idx, dept_idx, lv_idx, password, name, email, gender, hire_date 
FROM member 
WHERE user_id = #{user_id}

mapper.update:
UPDATE member 
SET password = #{password} 
WHERE user_id = #{user_id}
