mapper.insert:
INSERT INTO member (
    user_id, file_idx, dept_idx, lv_idx, password, name, email, gender, hire_date
) VALUES (
    #{user_id}, #{file_idx}, #{dept_idx}, #{lv_idx}, #{password}, #{name}, #{email}, #{gender}, #{hire_date}
)

mapper.update:
UPDATE member 
SET password = #{password} 
WHERE user_id = #{user_id}

UPDATE member 
SET email = #{email},
    password = #{password}
WHERE user_id = #{user_id}

mapper.select:
    	select * from member m
		join level l on m.lv_idx = l.lv_idx 
		join department d on d.dept_idx  = m.dept_idx ;

mapper.update:
UPDATE member SET lv_idx = #{lv_idx}, dept_idx = #{dept_idx}, status = #{status}, hire_end_date =
now()
#{hire_end_date}
WHERE user_id = #{user_id}
